import { app, BrowserWindow, ipcMain, Menu } from 'electron';
import dotenv from 'dotenv';

// Load environment variables
dotenv.config();
import { 
  get_all_notes, 
  create_note, 
  update_note, 
  delete_note, 
  get_note_with_entries,
  create_note_entry,
  update_note_entry,
  delete_note_entry,
  reorder_note_entries
} from './database/db';
import { processNoteEntryWithAI } from './services/ai-workflow';
import { INote, INoteEntry } from './shared/types';
// This allows TypeScript to pick up the magic constants that's auto-generated by Forge's Webpack
// plugin that tells the Electron app where to look for the Webpack-bundled app code (depending on
// whether you're running in development or production).
declare const MAIN_WINDOW_WEBPACK_ENTRY: string;
declare const MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY: string;
declare const CHILD_WINDOW_WEBPACK_ENTRY: string;
declare const CHILD_WINDOW_PRELOAD_WEBPACK_ENTRY: string;

// Handle creating/removing shortcuts on Windows when installing/uninstalling.
if (require('electron-squirrel-startup')) {
  app.quit();
}



let mainWindow: BrowserWindow;
const createWindow = (): void => {
  // Create the browser window.
  mainWindow = new BrowserWindow({
    height: 800,
    width: 1400,
    frame: false,
    webPreferences: {
      preload: MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY,
    },
  });

  // and load the index.html of the app.
  mainWindow.loadURL(MAIN_WINDOW_WEBPACK_ENTRY);

  /**
   * Mac - darwin
   * windows - win32
   * linux - linux
   */

  process.platform == 'darwin' && mainWindow.setWindowButtonVisibility(true);

  // Wait for the window to be ready before sending data
  mainWindow.webContents.once('did-finish-load', () => {
    get_all_notes((data: INote[]) => {
      mainWindow.webContents.send('onstart-notes-data', data || [])
    })
  })

  // Note operations
  ipcMain.handle('create-note', async (ev, title: string) => {
    return await new Promise((res, rej) => {
      create_note(title, "", (data: INote[]) => {
        res(data)
      })
    })
  })

  ipcMain.handle('update-note', async (ev, note: INote) => {
    return await new Promise((res, rej) => {
      update_note(note, (data: INote[]) => {
        res(data)
      })
    })
  })

  ipcMain.handle('delete-note', async (ev, noteId: number) => {
    return await new Promise((res, rej) => {
      delete_note(noteId, (data: INote[]) => {
        res(data)
      })
    })
  })

  ipcMain.handle('fetch-all-notes', async (ev, argz) => {
    return await new Promise((res, rej) => {
      get_all_notes((data: INote[]) => {
        res(data)
      })
    })
  })

  ipcMain.handle('get-note-with-entries', async (ev, noteId: number) => {
    return await new Promise((res, rej) => {
      get_note_with_entries(noteId, (data: INote) => {
        res(data)
      })
    })
  })

  // Note entry operations
  ipcMain.handle('create-note-entry', async (ev, noteId: number, heading: string, body: string) => {
    return await new Promise((res, rej) => {
      create_note_entry(noteId, heading, body, (data: INote) => {
        res(data)
      })
    })
  })

  ipcMain.handle('update-note-entry', async (ev, entry: INoteEntry) => {
    return await new Promise((res, rej) => {
      update_note_entry(entry, (data: INote) => {
        res(data)
      })
    })
  })

  ipcMain.handle('delete-note-entry', async (ev, entryId: number, noteId: number) => {
    return await new Promise((res, rej) => {
      delete_note_entry(entryId, noteId, (data: INote) => {
        res(data)
      })
    })
  })

  ipcMain.handle('reorder-note-entries', async (ev, noteId: number, entryIds: number[]) => {
    return await new Promise((res, rej) => {
      reorder_note_entries(noteId, entryIds, (data: INote) => {
        res(data)
      })
    })
  })

  // AI workflow operations
  ipcMain.handle('process-note-entry-with-ai', async (ev, noteTitle: string, entryHeading: string, apiKey?: string) => {
    try {
      const result = await processNoteEntryWithAI(noteTitle, entryHeading, apiKey);
      return result;
    } catch (error) {
      console.error("Error in AI workflow:", error);
      throw error;
    }
  })

  // AI summarization operations
  ipcMain.handle('summarize-note', async (ev, noteId: number, summary: string) => {
    return await new Promise((res, rej) => {
      // First get the note
      get_note_with_entries(noteId, (note: INote) => {
        if (!note) {
          rej(new Error('Note not found'));
          return;
        }
        
        // Update the note with the summary
        const updatedNote = { ...note, summary };
        update_note(updatedNote, (updatedNotes: INote[]) => {
          res(updatedNotes);
        });
      });
    });
  })

  // Window controls
  ipcMain.on('close-app', (ev, argz) => {
    mainWindow.close()
  })

  ipcMain.on('maximize-app', (ev, argz) => {
    mainWindow.isMaximized() ? mainWindow.unmaximize() : mainWindow.maximize()
  })

  ipcMain.on('minimize-app', (ev, argz) => {
    mainWindow.minimize()
  })
};

const isMac = process.platform === 'darwin'

const template = [
  // { role: 'appMenu' }
  ...(isMac
    ? [{
        label: app.name,
        submenu: [
          { role: 'about' },
          { type: 'separator' },
          { role: 'services' },
          { type: 'separator' },
          { role: 'hide' },
          { role: 'hideOthers' },
          { role: 'unhide' },
          { type: 'separator' },
          { role: 'quit' }
        ]
      }]
    : []),
  // { role: 'fileMenu' }
  {
    label: 'File',
    submenu: [
      {label: "New note", click: () => {
        create_note("New Note", "", (data: INote[]) => {
          mainWindow.webContents.send('update-notes-data', data);
        })
      }},
      isMac ? { role: 'close' } : { role: 'quit' },
    ]
  },
  // { role: 'editMenu' }
  {
    label: 'Edit',
    submenu: [
      { role: 'undo' },
      { role: 'redo' },
      { type: 'separator' },
      { role: 'cut' },
      { role: 'copy' },
      { role: 'paste' },
      ...(isMac
        ? [
            { role: 'pasteAndMatchStyle' },
            { role: 'delete' },
            { role: 'selectAll' },
            { type: 'separator' },
            {
              label: 'Speech',
              submenu: [
                { role: 'startSpeaking' },
                { role: 'stopSpeaking' }
              ]
            }
          ]
        : [
            { role: 'delete' },
            { type: 'separator' },
            { role: 'selectAll' }
          ])
    ]
  },
  // { role: 'viewMenu' }
  {
    label: 'View',
    submenu: [
      { role: 'reload' },
      { role: 'forceReload' },
      { role: 'toggleDevTools' },
      { type: 'separator' },
      { role: 'resetZoom' },
      { role: 'zoomIn' },
      { role: 'zoomOut' },
      { type: 'separator' },
      { role: 'togglefullscreen' }
    ]
  },
  // { role: 'windowMenu' }
  {
    label: 'Window',
    submenu: [
      { role: 'minimize' },
      { role: 'zoom' },
      ...(isMac
        ? [
            { type: 'separator' },
            { role: 'front' },
            { type: 'separator' },
            { role: 'window' }
          ]
        : [
            { role: 'close' }
          ])
    ]
  },
  {
    role: 'help',
    submenu: [
      {
        label: 'Learn More',
        click: async () => {
          const { shell } = require('electron')
          await shell.openExternal('https://electronjs.org')
        }
      }
    ]
  }
] as any

const menu = Menu.buildFromTemplate(template)
Menu.setApplicationMenu(menu)

ipcMain.on('open-note-in-child-process', (ev, noteId: number) => {
  const childWindow = new BrowserWindow({
    height: 450,
    width: 450,
    frame: false,
    webPreferences: {
      preload: CHILD_WINDOW_PRELOAD_WEBPACK_ENTRY,
    },
  })
  childWindow.loadURL(CHILD_WINDOW_WEBPACK_ENTRY+"?note_id="+noteId);
})

ipcMain.on('open_note_item_context_menu', (ev, noteId: number) => {
  const template = [
    {
      label: 'Open in a new window',
      click: () => { 
        ipcMain.emit('open-note-in-child-process', noteId)
      }
    },
    { type: 'separator' },
    { label: 'Delete', click: () => {
      delete_note(noteId, (data: INote[]) => {
        ev.sender.send('update-notes-data', data)
      })
    } }
  ] as any
  const menu = Menu.buildFromTemplate(template)
  menu.popup({ window: BrowserWindow.fromWebContents(ev.sender) })
})

// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.on('ready', createWindow);

// Quit when all windows are closed, except on macOS. There, it's common
// for applications and their menu bar to stay active until the user quits
// explicitly with Cmd + Q.
app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});

app.on('activate', () => {
  // On OS X it's common to re-create a window in the app when the
  // dock icon is clicked and there are no other windows open.
  if (BrowserWindow.getAllWindows().length === 0) {
    createWindow();
  }
});

